import 'package:encrypt/encrypt.dart';
import 'package:basic_utils/basic_utils.dart';

void main() async {
  final publicKey = CryptoUtils.rsaPublicKeyFromPem(X509Utils.formatKeyString(
      '''''',
      X509Utils.BEGIN_PKCS7,
      X509Utils.END_PKCS7));
  final plainText = 'la4as9WYlhwu72qp5pK2m4PwqW8h9wYO';
  Encrypter encrypter;
  Encrypted encrypted;

  encrypter = Encrypter(RSA(publicKey: publicKey));
  encrypted = encrypter.encrypt(plainText);

  print(encrypted.base64);
}
